def route_controller(app: FastAPI, mb: MessageBus, storage_path: str):

    @app.post('/predict')
    def get_predict(param: PredictParam):
        try:
            model = Model(storage_path, param.model_file_name)
            return {'result': model.predict(param.data)}
        except Exception as error:
            print(traceback.format_exc()) 
            raise HTTPException(status_code=500, detail='Internal Server Error')
    print('Handle routes for ml')


